import React, { Component } from 'react';
import {Marker, MapContainer, GeoJSON, TileLayer,Popup} from "react-leaflet"
import county_ca from './../data/county_ca.json'
import 'leaflet/dist/leaflet.css';
import L from 'leaflet'
import markers from './../data/markers.json'
import icon from './marker.png';

let DefaultIcon = L.icon({
    iconUrl: icon,
    iconSize:[20,20],
    iconAnchor:[17,20]
});

var coordinates = [];

L.Marker.prototype.options.icon = DefaultIcon;

class MyMap extends Component {
    constructor () {
        this.super();
        state = {
            counties:[],
            markers:[]
        };
}

    componentDidMount() {

        for (var i = 0; i < markers.length; i++) {
            this.markers.push([markers[i].latitude, markers[i].longitude]);
        }

        //this.setState({markers:coordinates});
        console.log(county_ca);
    }

    getColor = (n) => {
        if(n > 2){
            return "red";
        }
        else{
            return "blue";
        }

    };

    countyStyle = {
        fillColor: "blue",
        fillOpacity: .6,
        color: "black",
        weight:.8,

    };


    onEachCounty = (county, layer) =>{
        const countyName = county.properties.NAME;
        layer.bindPopup(`${countyName}`)

        if(county.properties.COUNTYFP > 34){
            layer.options.fillColor = "green"
        }


    };



    render() {

        return ( 
            <div>
                <MapContainer style = {{height: "80vh", width: '80%', margin: 'auto'}} zoom = {6} center = {[38, -122]} scrollWheelZoom = {false}>
                    <GeoJSON style = {this.countyStyle} data ={county_ca.features} onEachFeature={this.onEachCounty}/>
                    <TileLayer
                     attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                     url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                     />
                    {this.state.markers.map((position, idx) => 
                        <Marker key={`marker-${idx}`} position={position}>
                        <Popup>
                            <span>Popup</span>
                        </Popup>
                        </Marker>
                    )}

                </MapContainer>
            </div>
        );
    }
}

export default MyMap
